import React from 'react';

class BuilingList extends React.Component {


	filterBuildingName = (buildingNameFilter) => {
	    let filteredBuildings = this.state.filterText;
	    filteredBuildings = filteredBuildings.filter((building) => {
	      let name = building.name.toLowerCase()
	      return name.indexOf(buildingNameFilter.toLowerCase()) !== -1
	    })
	    this.setState({
	      filteredBuildings
	    })
	  }

	tappedBuilding = (building) => {
		console.log(building.name + ' ID: ' + building.id);
	}

	render() {
		// console.log('This is my directory file', this.props.data);
		const { data, filter } = this.props;
		console.log("FILTER: " + filter);
		var input = filter.toLowerCase();

		const buildingList = data.filter((building, i) => {
				var name = data[i].name.toLowerCase();
				console.log("\tdata[" + name + "]: " + (name.indexOf(input) !== 1))
				return (name.indexOf(input) !== 1);
	    }).map(directory => {
			return (
				<tr key={directory.id} >
					<td>{directory.code} </td>
					<td onClick={()=>this.tappedBuilding(directory)}> {directory.name} </td>
				</tr>
			);
		});

		return <div>{buildingList}</div>;
	}
}
export default BuilingList;
